	IsRemoved bit DEFAULT 0 NOT NULL,    --  是否已经被逻辑移除。1/0。 
	Visible bit DEFAULT 1 NOT NULL,    --  是否可见。1/0。 
	IsDefault bit DEFAULT 1 NOT NULL,    --  是否为默认的对象。1/0。 
	Actived bit DEFAULT 1 NOT NULL,    --  是否已经被激活。 
	SortId int DEFAULT 0 NOT NULL,    --  排序标记。 
	Version decimal(19,2) DEFAULT 1.00 NOT NULL,    --  版本号。 
	IsLocked bit DEFAULT 0 NOT NULL,    --  是否被锁定。1/0。 
	IsFrozen bit DEFAULT 0 NOT NULL,    --  是否被冻结。 
	CreatedTime datetime DEFAULT Getdate() NOT NULL,    --  对象的创建时间。 
	LastModifiedTime datetime DEFAULT Getdate() NOT NULL,    --  最后一次被修改的时间。 
	ModifiedTimes int DEFAULT 0 NOT NULL,    --  对象信息被修改的次数。 
	ExtendedAttributes xml DEFAULT NULL,    --  扩展的属性。 
	CompatibleExtendedAttributes nvarchar(max) DEFAULT NULL,    --  为了保证兼容性的扩展属性。 
	Description nvarchar(max) DEFAULT NULL,    --  描述信息。 
	Comments nvarchar(max) DEFAULT NULL    --  备注信息。 
)
;


